#include <gmp.h>
#include <stdio.h>
#include <stdlib.h>

// gcc -Wall -Wextra -o gmp_examples gmp_examples.c -lgmp

static void integer_example1(void)
{
  mpz_t m;
  mpz_t n;
  mpz_t pa;
  mpz_t pm;

  /*
  ** Initialize some integers.
  */

  mpz_init(m);
  mpz_init(n);
  mpz_init(pa);
  mpz_init(pm);

  /*
  ** Populate m and n with integer values.
  */

  mpz_init_set_str(m, "", 10);
  mpz_init_set_str(n, "", 10);

  /*
  ** Some arithmetic.
  */

  mpz_add(pa, m, n); // pa = m + n
  mpz_mul(pm, m, n); // pm = m * n

  /*
  ** Display results.
  */

  printf("m + n = ");
  mpz_out_str(stdout, 10, pa);
  printf("\n");
  printf("m * n = ");
  mpz_out_str(stdout, 10, pm);
  printf("\n");

  if(mpz_cmp(m, n) > 0)
    printf("m is greater than n.\n");
  else
    printf("m is less than or equal to n.\n");

  /*
  ** Release resources.
  */

  mpz_clear(m);
  mpz_clear(n);
  mpz_clear(pa);
  mpz_clear(pm);
}

int main(void)
{
  integer_example1();
  return EXIT_SUCCESS;
}
